import PinYin from "../src/wl-pinyin";

test("get first letter", () => {
    expect(PinYin.getFirstLetter("你好")).toBe("NH");
});

test("get word's pinyin", () => {
    expect(PinYin.getPinyin("你好")).toBe("ni hao");
});

test(`get word's pinyin to use ", "`, () => {
    expect(PinYin.getPinyin("你好", ", ")).toBe("ni, hao");
});
